function criarNomeSeguro(texto) {
    if (!texto) return '';
    return texto
        .toLowerCase()
        .normalize("NFD").replace(/[\u0300-\u036f]/g, "") 
        .replace(/ç/g, "c") 
        .replace(/\s+/g, '_') 
        .replace(/[^a-z0-9_]/g, ''); 
}
/**
 * NOVO (em falta): Desenha a tabela de "Viatura Pronta para Entrega" na interface.
 * Lê os dados da variável global 'viaturaProntaData' e constrói as linhas da tabela.
 */
function renderViaturaProntaTable() {
    const tbody = document.querySelector("#verificacao_entrega_details_table tbody");
    if (!tbody) return;

    tbody.innerHTML = ''; // Limpa a tabela antes de redesenhar

    viaturaProntaData.forEach((item, index) => {
        const row = tbody.insertRow();
        row.setAttribute('data-index', index);

        // Célula 1: Verificação (Texto ou Textarea)
        const cellVerificacao = row.insertCell(0);
        if (item.isManual) {
            const textarea = document.createElement('textarea');
            textarea.value = item.verificacao;
            textarea.placeholder = "Descreva o ponto de verificação...";
            textarea.rows = 2;
            textarea.oninput = (e) => {
                viaturaProntaData[index].verificacao = e.target.value;
                guardarEstadoFormulario(e);
            };
            cellVerificacao.appendChild(textarea);
        } else {
            cellVerificacao.textContent = item.verificacao;
        }

        // Célula 2: Estado (Dropdown)
        const cellEstado = row.insertCell(1);
        const select = document.createElement('select');
        ['', 'OK', 'NOK', 'NA'].forEach(val => {
            const option = document.createElement('option');
            option.value = val;
            option.textContent = val;
            select.appendChild(option);
        });
        select.value = item.estado;
        select.onchange = (e) => {
            viaturaProntaData[index].estado = e.target.value;
            guardarEstadoFormulario(e); 
            updateVerificacaoEntregaSummary();
        };
        cellEstado.appendChild(select);

        // Célula 3: Ações (Botão de Remover para linhas manuais)
        const cellAcoes = row.insertCell(2);
        if (item.isManual) {
            const removeButton = document.createElement('button');
            removeButton.type = 'button';
            removeButton.textContent = 'Remover';
            removeButton.className = 'btn-remover-linha';
            removeButton.onclick = () => {
                viaturaProntaData.splice(index, 1);
                renderViaturaProntaTable();
                updateVerificacaoEntregaSummary();
            };
            cellAcoes.appendChild(removeButton);
        }
    });

    updateVerificacaoEntregaSummary();
}

/**
 * Converte uma string de data (AAAA-MM-DD ou DD/MM/AAAA) para um objeto Date.
 * @param {string} dateString - A data em formato de texto.
 * @returns {Date|null} - O objeto Date ou null se o formato for inválido.
 */
function parseDateString(dateString) {
    if (!dateString || typeof dateString !== 'string') return null;

    let dateObj = null;
    try {
        if (dateString.includes('/')) {
            // Assume formato DD/MM/AAAA
            const parts = dateString.split('/');
            if (parts.length === 3) {
                // new Date(ano, mês - 1, dia)
                dateObj = new Date(parts[2], parts[1] - 1, parts[0]);
            }
        } else if (dateString.includes('-')) {
            // Assume formato AAAA-MM-DD
            dateObj = new Date(dateString);
        }
        
        // Se a data for inválida (ex: por causa de um texto aleatório), retorna null
        if (dateObj && isNaN(dateObj.getTime())) {
            return null;
        }
        return dateObj;

    } catch (e) {
        return null;
    }
}
function formatarDataParaMesAno(dateString) {
    const dataObj = parseDateString(dateString);
    if (!dataObj) return null;
    const mes = String(dataObj.getMonth() + 1).padStart(2, '0');
    const ano = dataObj.getFullYear();
    return `${mes}/${ano}`;
}
