async function carregarPenalizacoes() {
    try {
        const response = await fetch('Penalizações.csv');
        const csvText = await response.text();
        const linhas = csvText.split('\n').slice(1); // Ignora a primeira linha (cabeçalho)
        
        const dados = linhas.map(linha => {
            const [artigo, descricao, valor, valorVariavel] = linha.split(';');
            if (!artigo) return null; // Ignora linhas vazias
            return {
                artigo: artigo.trim(),
                descricao: descricao.trim(),
                valor: valor.trim(),
                valorVariavel: valorVariavel ? valorVariavel.trim().toLowerCase() === 'true' : false
            };
        }).filter(Boolean); // Remove linhas nulas

        console.log("Penalizações carregadas com sucesso.", dados);
        return dados;
    } catch (error) {
        console.error("Erro ao carregar o ficheiro de Penalizações:", error);
        return []; // Retorna um array vazio em caso de erro
    }
}

