/**
 * FUNÇÃO ÚNICA E DEFINITIVA: Desenha a pré-visualização para QUALQUER imagem.
 */
function desenharPreview(container, imgData, inputId, index) {
    if (!container) return;

    const imageUrl = typeof imgData === 'object' ? imgData.url : imgData;
    if (!imageUrl) return;

    const legendaAtual = typeof imgData === 'object' ? imgData.legenda : undefined;
    const isRentabilidade = inputId === 'rentabilidade_imagem_upload';
    
    const idsComLegenda = ['fotos_aspeto_visual', 'fotos_seguranca_ambiente', 'fotos_verificacao_entrega', 'anexar_diversos_files', 'fotos_gerais_stock_pecas', 'fotos_caixas_pecas', 'fotos_caixas_oficina', 'rentabilidade_imagem_upload'];

    const imageGroup = document.createElement('div');
    
    imageGroup.className = 'image-group';
    imageGroup.style.width = '100%';
    imageGroup.style.marginBottom = '10px';
    imageGroup.style.display = 'flex';
    imageGroup.style.alignItems = 'flex-start';
    imageGroup.draggable = isRentabilidade;
    imageGroup.dataset.index = index;

    const img = document.createElement('img');
    img.src = imageUrl;
    img.style.cursor = 'pointer';
    img.onclick = () => abrirImagemModal(imageUrl);
    img.style.width = '150px';
    img.style.height = '100px';
    img.style.objectFit = 'cover';
    img.style.marginRight = '10px';
    
    const controlsContainer = document.createElement('div');
    controlsContainer.style.flexGrow = '1';
    controlsContainer.style.display = 'flex';
    controlsContainer.style.flexDirection = 'column';

    if (idsComLegenda.includes(inputId) && legendaAtual !== undefined) {
        const legendaInput = document.createElement('input');
        legendaInput.type = 'text';
        legendaInput.placeholder = 'Legenda...';
        legendaInput.value = legendaAtual || '';
        legendaInput.style.width = '100%';
        legendaInput.style.marginBottom = '5px';
        legendaInput.oninput = () => {
            imagensCarregadasParaPDF[inputId][index].legenda = legendaInput.value;
            formularioAlterado = true;
        };
        controlsContainer.appendChild(legendaInput);
    }
    
    const buttonDiv = document.createElement('div');

    const removeButton = document.createElement('button');
    removeButton.type = 'button';
    removeButton.textContent = 'Remover';
    removeButton.className = 'btn-remover-linha';
    removeButton.onclick = () => {
        if (confirm("Tem a certeza que deseja apagar esta imagem permanentemente?")) {
            const imageRef = ref(storage, imageUrl);
            deleteObject(imageRef).catch(error => console.error("Erro ao apagar do Storage:", error));
            imagensCarregadasParaPDF[inputId].splice(index, 1);
            redesenharPreviews(inputId);
            salvarSecaoNaCloud(container.closest('.tabcontent').id);
        }
    };
    buttonDiv.appendChild(removeButton);

    if (isRentabilidade) {
        const topButton = document.createElement('button');
        topButton.type = 'button';
        topButton.textContent = 'Para o Topo';
        topButton.className = 'btn-acao-linha';
        topButton.style.marginLeft = '5px';
        topButton.onclick = () => {
            if (index > 0) {
                const [itemMovido] = imagensCarregadasParaPDF[inputId].splice(index, 1);
                imagensCarregadasParaPDF[inputId].unshift(itemMovido);
                redesenharPreviews(inputId);
                salvarSecaoNaCloud(container.closest('.tabcontent').id);
            }
        };
        buttonDiv.appendChild(topButton);
    }
    
    controlsContainer.appendChild(buttonDiv);
    imageGroup.appendChild(img);
    imageGroup.appendChild(controlsContainer);
    container.appendChild(imageGroup);
}

// VERSÃO FINAL E UNIFICADA
function redesenharPreviews(inputId) {
    const previewContainerId = (inputId === 'rentabilidade_imagem_upload')
        ? 'rentabilidade_imagem_preview' 
        : `preview_${inputId}`;
    const previewContainer = document.getElementById(previewContainerId);
    if (!previewContainer) return;
    
    previewContainer.innerHTML = ''; 
    if(inputId === 'rentabilidade_imagem_upload') {
        previewContainer.insertAdjacentHTML('afterbegin', '<p style="color: #888; font-style: italic;">Arraste as imagens para reordenar.</p>');
    }

    const imagens = imagensCarregadasParaPDF[inputId] || [];
    
    // O ciclo agora chama sempre a mesma função 'desenharPreview'
    imagens.forEach((imgData, index) => {
        if (!imgData) return; // Ignora entradas nulas/inválidas
        desenharPreview(previewContainer, imgData, inputId, index);
    });
}

async function carregarOficinas() {
    try {
        const response = await fetch('Oficinas.csv');
        const csvText = await response.text();
        
        const oficinasCompletas = csvText.split('\n')
            .map(linha => linha.split(';')[0].trim()) // <--- ALTERAÇÃO AQUI
            .filter(Boolean)
            .sort();

        const locaisUnicos = Array.from(new Set(
            oficinasCompletas.map(oficina => {
                const parts = oficina.split(' ');
                return parts.slice(-2).join(' ');
            })
        )).sort();

        console.log("Oficinas e Locais carregados com sucesso.");
        return {
            oficinas: oficinasCompletas,
            locais: locaisUnicos
        };
    } catch (error) {
        console.error("Erro ao carregar o ficheiro de Oficinas:", error);
        return { oficinas: [], locais: [] };
    }
}

