function calcularResumoProtecoes() {
    const tableBody = document.querySelector("#verificacao_protecoes_table tbody");
    if(!tableBody) return;
    
    const rows = tableBody.rows;
    const numViaturasAuditadas = rows.length;
    let totalItens = 0, itensOk = 0, itensNok = 0, viaturasOk = 0, viaturasNok = 0;

    Array.from(rows).forEach(row => {
        let isViaturaCurrentlyOk = true;
        let hasNokItemInViatura = false;
        
        // As células de proteção são da 4ª à 9ª (índices 3 a 8)
        for (let i = 3; i <= 8; i++) {
            const select = row.cells[i].querySelector('select');
            if (select) {
                const value = select.value;
                if (value === 'OK') {
                    totalItens++;
                    itensOk++;
                } else if (value === 'NOK') {
                    totalItens++;
                    itensNok++;
                    hasNokItemInViatura = true;
                }
            }
        }
        
        if (hasNokItemInViatura) {
            viaturasNok++;
        } else if (numViaturasAuditadas > 0) { // Só conta como OK se não tiver NOK
            viaturasOk++;
        }
    });

    const percentItensOk = totalItens > 0 ? ((itensOk / totalItens) * 100).toFixed(2) : '0.00';
    const percentItensNok = totalItens > 0 ? ((itensNok / totalItens) * 100).toFixed(2) : '0.00';
    const percentViaturasOk = numViaturasAuditadas > 0 ? ((viaturasOk / numViaturasAuditadas) * 100).toFixed(2) : '0.00';
    const percentViaturasNok = numViaturasAuditadas > 0 ? ((viaturasNok / numViaturasAuditadas) * 100).toFixed(2) : '0.00';

    const resumoTableBody = document.querySelector("#resumo_protecoes_summary_table tbody");
    if(!resumoTableBody) return;

    if (resumoTableBody.rows.length === 0) {
        resumoTableBody.insertRow();
    }
    const summaryRow = resumoTableBody.rows[0];
    const dataCorreta = document.getElementById('resumo_protecoes_data').value;
    summaryRow.innerHTML = `
        <td><input type="text" value="${numViaturasAuditadas}" readonly></td>
        <td><input type="text" value="${percentViaturasOk}%" readonly></td>
        <td><input type="text" value="${percentViaturasNok}%" readonly></td>
        <td><input type="text" value="${percentItensOk}%" readonly></td>
        <td><input type="text" value="${percentItensNok}%" readonly></td>
    `;
}

