// Functions for Avaliação de Concessão table
function populateAvaliacaoConcessaoTable(initialData = {}) {
    // --- Lógica de cálculo de peso DINÂMICO ---
    const checkboxes = document.querySelectorAll('#vertentes-rentabilidade-container input[type="checkbox"]');
    const vertentesAtivas = Array.from(checkboxes).filter(cb => cb.checked);
    const pesoTotalRentabilidade = 5; // O total a ser distribuído

    // Calcula o peso por cada vertente ativa. Se nenhuma estiver ativa, o peso é 0.
    const pesoPorVertente = vertentesAtivas.length > 0 ? (pesoTotalRentabilidade / vertentesAtivas.length) : 0;

    // Cria um mapa para saber quais checkboxes estão ativos
    const nomesVertentesAtivas = new Set(vertentesAtivas.map(cb => cb.value));

    // Atualiza o peso de cada item de rentabilidade na estrutura principal
    avaliacaoConcessaoItems.forEach(item => {
        // Verifica se o 'name' do item corresponde a uma das vertentes
        if (item.name && item.name.startsWith('of_')) {
            const isVertente = ['of_mod_mecanica', 'of_mod_chapa', 'of_mod_pintura', 'of_pecas', 'of_material_pintura', 'of_servicos_externos'].includes(item.name);
            if (isVertente) {
                item.weight = nomesVertentesAtivas.has(item.name) ? pesoPorVertente : 0;
            }
        }
    });

    // --- Lógica de desenho da tabela ---
    const tableBody = document.querySelector("#avaliacao_concessao_table tbody");
    if (tableBody) {
    tableBody.innerHTML = '';

    avaliacaoConcessaoItems.forEach(item => {
        const newRow = tableBody.insertRow();

        if (item.isCategory || item.isSubCategory) {
            const cell = newRow.insertCell();
            cell.colSpan = 5;
            cell.innerHTML = `<span style="padding-left: ${item.level * 15}px;"><b>${item.text}</b></span>`;
            newRow.className = item.isCategory ? 'category-row' : 'sub-category-row';
        } else {
            // Desenha a linha de item normal
            const itemTextCell = newRow.insertCell(0);
            const pesoCell = newRow.insertCell(1);
            const conformityValueCell = newRow.insertCell(2);
            const pontuacaoObtidaCell = newRow.insertCell(3);
            const obsCell = newRow.insertCell(4);

            itemTextCell.innerHTML = `<span style="padding-left: ${item.level * 15}px;">${item.text}</span>`;
            pesoCell.textContent = item.weight.toFixed(2);

            let inputCriado = null; // Variável para guardar a referência ao input ou select

            if (item.inputType === "select") {
                const select = document.createElement('select');
                select.name = item.name;
                select.setAttribute('data-item-name', item.name);
                select.innerHTML = '<option value="">Selecione...</option>';
                item.options.forEach(opt => select.innerHTML += `<option value="${opt}">${opt}</option>`);
                select.onchange = calculateAvaliacaoConcessaoTotal;
                conformityValueCell.appendChild(select);
                inputCriado = select;
            } else if (item.inputType === "number") {
                const input = document.createElement('input');
                input.type = "number";
                input.name = item.name;
                input.setAttribute('data-item-name', item.name);
                input.min = "0";
                if (item.text.includes('%')) input.max = "100";
                input.oninput = calculateAvaliacaoConcessaoTotal;
                conformityValueCell.appendChild(input);
                inputCriado = input;
            }

            const pontuacaoInput = document.createElement('input');
            pontuacaoInput.type = "text";
            pontuacaoInput.readOnly = true;
            pontuacaoInput.className = 'pontuacao-obtida';
            pontuacaoObtidaCell.appendChild(pontuacaoInput);
            
            const obsTextarea = document.createElement('textarea');
            obsTextarea.rows = "1";
            obsTextarea.name = `${item.name}_obs`;
            obsTextarea.setAttribute('data-item-name', item.name);
            obsTextarea.className = 'observacoes-textarea';
            obsCell.appendChild(obsTextarea);

            // --- Preencher os dados AQUI, dentro do loop ---
            if (initialData && initialData[item.name]) {
                const itemData = initialData[item.name];
                if (inputCriado) {
                    inputCriado.value = itemData.value || '';
                }
                pontuacaoInput.value = itemData.pontuacao_obtida || '';
                obsTextarea.value = itemData.observacoes || '';
            }
        }
    });
    }
    // Atribui o "ouvinte" de evento FORA do loop principal
    checkboxes.forEach(cb => {
        cb.removeEventListener('change', populateAvaliacaoConcessaoTable);
        cb.addEventListener('change', populateAvaliacaoConcessaoTable);
    });

    // Preenche o total geral se vier do JSON
    // CORREÇÃO: Usar o nome da propriedade exato que deve ser recebido
    if (initialData.total_geral_avaliacao_concessao) { 
        document.getElementById('total_geral_avaliacao_concessao').value = initialData.total_geral_avaliacao_concessao;
    }

    // Recalcula o total para garantir consistência
    calculateAvaliacaoConcessaoTotal();
}

function calculateAvaliacaoConcessaoTotal() {
    let totalGeralPontuacao = 0;
    const tableRows = document.querySelectorAll("#avaliacao_concessao_table tbody tr");

    tableRows.forEach(row => {
        const itemNameElement = row.querySelector('[data-item-name]');
        
        if (!itemNameElement) return; // Skip category/subcategory rows

        const itemName = itemNameElement.getAttribute('data-item-name');
        const itemConfig = avaliacaoConcessaoItems.find(item => item.name === itemName);
        if (!itemConfig) return;

        const conformityInput = row.querySelector(`[name="${itemName}"]`);
        const pontuacaoOutput = row.querySelector('.pontuacao-obtida');
        
        let pontuacao = 0;

        if (itemConfig.inputType === "select") {
            if (conformityInput.value === "OK") {
                pontuacao = itemConfig.weight;
            } else if (conformityInput.value === "NOK") {
                pontuacao = 0; // Explicitly 0 for NOK
            } else {
                pontuacao = 0; // For N/A or empty, consider 0 contribution
            }
            pontuacaoOutput.value = pontuacao.toFixed(2);
            totalGeralPontuacao += pontuacao;

        } else if (itemConfig.inputType === "number") {
            let valorConformidade = parseFloat(conformityInput.value);
            if (!isNaN(valorConformidade) && itemConfig.weight !== undefined) {
                // Assuming the input number is a percentage (0-100)
                pontuacao = (valorConformidade / 100) * itemConfig.weight;
                totalGeralPontuacao += pontuacao;
                pontuacaoOutput.value = pontuacao.toFixed(2);
            } else {
                pontuacaoOutput.value = '';
            }
        }
    });

    document.getElementById('total_geral_avaliacao_concessao').value = totalGeralPontuacao.toFixed(2);
}


