// NEW: Função para atualizar o campo "Conclusão" de uma viatura específica
function updateConclusaoViatura(row) {
    let allOkOrNA = true; 
    let hasNok = false;   

    // As células de proteção são da 4ª à 9ª (índices 3 a 8) - Volante, Banco, Tapete, Travão Mão, Mudanças
    for (let i = 3; i <= 8; i++) { 
        const select = row.cells[i]?.querySelector('select'); // Adicionado '?' para robustez
        if (select) {
            const value = select.value;
            if (value === 'NOK') {
                hasNok = true;
                allOkOrNA = false; 
                break; 
            } else if (value !== 'OK' && value !== 'N/A') {
                allOkOrNA = false;
            }
        }
    }

    const conclusaoTextarea = row.cells[8]?.querySelector('textarea'); // Adicionado '?' para robustez
    if (conclusaoTextarea) { // Garante que o elemento existe antes de manipulá-lo
        if (hasNok) {
            conclusaoTextarea.value = 'NOK';
            conclusaoTextarea.style.color = 'red'; 
            conclusaoTextarea.style.fontWeight = 'bold';
        } else if (allOkOrNA) {
            conclusaoTextarea.value = 'OK';
            conclusaoTextarea.style.color = 'green'; 
            conclusaoTextarea.style.fontWeight = 'bold';
        } else {
            conclusaoTextarea.value = ''; 
            conclusaoTextarea.style.color = 'black';
            conclusaoTextarea.style.fontWeight = 'normal';
        }
    }
    calcularResumoProtecoes(); 
}

window.adicionarLinhaProtecaoViatura = function(valores = {}) {
    const tableBody = document.querySelector("#verificacao_protecoes_table tbody");
    const newRow = tableBody.insertRow();
    const rowCount = tableBody.rows.length;
    let cell;

    const createInput = (type, namePrefix, value = '') => {
        const input = document.createElement('input');
        input.type = type;
        input.name = `${namePrefix}_${rowCount}`;
        input.value = value || '';
        input.oninput = guardarEstadoFormulario;
        return input;
    };

    const createSelect = (namePrefix, options, value = '') => {
        const select = document.createElement('select');
        select.name = `${namePrefix}_${rowCount}`;

        const placeholderOption = document.createElement('option');
        placeholderOption.value = "";
        placeholderOption.textContent = "Selecione...";
        select.appendChild(placeholderOption);

        options.forEach(optionText => {
            const option = document.createElement('option');
            option.value = optionText;
            option.textContent = optionText;
            select.appendChild(option);
        });
        select.value = value || '';
        select.oninput = guardarEstadoFormulario;
        select.addEventListener('change', () => {
            updateConclusaoViatura(newRow);
            calcularResumoProtecoes();
            guardarEstadoFormulario();
        });
        return select;
    };

    const createTextarea = (namePrefix, rows = 2, value = '') => {
        const textarea = document.createElement('textarea');
        textarea.name = `${namePrefix}_${rowCount}`;
        textarea.rows = rows;
        textarea.value = value || '';
        textarea.oninput = guardarEstadoFormulario;
        return textarea;
    };

    // Matrícula
    cell = newRow.insertCell();
    cell.setAttribute('data-label', 'Matrícula');
    cell.appendChild(createInput('text', 'matricula', valores.matricula));

    // Marca
    cell = newRow.insertCell();
    cell.setAttribute('data-label', 'Marca');
    cell.appendChild(createInput('text', 'marca', valores.marca));

    // Modelo
    cell = newRow.insertCell();
    cell.setAttribute('data-label', 'Modelo');
    cell.appendChild(createInput('text', 'modelo', valores.modelo));

    // Proteções (Volante, Banco, Tapete, Travão Mão, Mudanças)
    const protectionOptions = ['OK', 'NOK', 'N/A'];
    cell = newRow.insertCell();
    cell.setAttribute('data-label', 'Volante');
    cell.appendChild(createSelect('protecao_volante', protectionOptions, valores.volante));
    
    cell = newRow.insertCell();
    cell.setAttribute('data-label', 'Banco');
    cell.appendChild(createSelect('protecao_banco', protectionOptions, valores.banco));
    
    cell = newRow.insertCell();
    cell.setAttribute('data-label', 'Tapete');
    cell.appendChild(createSelect('protecao_tapete', protectionOptions, valores.tapete));
    
    cell = newRow.insertCell();
    cell.setAttribute('data-label', 'Travão Mão');
    cell.appendChild(createSelect('protecao_travao_mao', protectionOptions, valores.travao_mao));
    
    cell = newRow.insertCell();
    cell.setAttribute('data-label', 'Mudanças');
    cell.appendChild(createSelect('protecao_mudancas', protectionOptions, valores.mudancas));

    // Conclusão
    cell = newRow.insertCell();
    cell.setAttribute('data-label', 'Conclusão');
    cell.appendChild(createTextarea('conclusao', 2, valores.conclusao));

    // Botão de Ação (Remover Linha)
    cell = newRow.insertCell();
    cell.setAttribute('data-label', 'Ações');
    const removeButton = document.createElement('button');
    removeButton.type = 'button';
    removeButton.textContent = 'Remover';
    removeButton.className = 'remove-image-button';
    removeButton.onclick = function() {
        newRow.remove();
        calcularResumoProtecoes();
        guardarEstadoFormulario();
    };
    cell.appendChild(removeButton);

    // Chamada inicial para atualizar a conclusão da linha recém-adicionada
    updateConclusaoViatura(newRow);

    return newRow;
}
/**
 * Exibe o nome do ficheiro selecionado num elemento span.
 * @param {HTMLInputElement} input O elemento input de tipo 'file'.
 * @param {string} spanId O ID do elemento span onde o nome do ficheiro será exibido.
 */
function displayFileName(input, spanId) {
    const spanElement = document.getElementById(spanId);
    if (spanElement && input.files.length > 0) {
        spanElement.textContent = input.files[0].name;
    } else if (spanElement) {
        spanElement.textContent = 'Nenhum ficheiro selecionado.';
    }
}

function redesenharTabelaFerramentas() {
    const tbody = document.getElementById('ferramentas_especiais_tbody');
    if (!tbody) return;
    tbody.innerHTML = '';
    (ferramentasEspeciaisData || []).forEach((rowData, idx) => {
        const row = tbody.insertRow();
        // Referência
        let cell = row.insertCell();
        let input = document.createElement('input');
        input.type = 'text';
        input.value = rowData.referencia || '';
        input.oninput = function() {
            ferramentasEspeciaisData[idx].referencia = this.value;
            guardarEstadoFormulario();
        };
        cell.appendChild(input);
        // Contagem esperada
        cell = row.insertCell();
        input = document.createElement('input');
        input.type = 'number';
        input.value = rowData.contagem_esperada || '';
        input.oninput = function() {
            ferramentasEspeciaisData[idx].contagem_esperada = this.value;
            guardarEstadoFormulario();
        };
        cell.appendChild(input);
        // Contagem efetuada
        cell = row.insertCell();
        input = document.createElement('input');
        input.type = 'number';
        input.value = rowData.contagem_efetuada || '';
        input.oninput = function() {
            ferramentasEspeciaisData[idx].contagem_efetuada = this.value;
            guardarEstadoFormulario();
        };
        cell.appendChild(input);
        // Ações
        cell = row.insertCell();
        const btn = document.createElement('button');
        btn.type = 'button';
        btn.textContent = 'Remover';
        btn.onclick = function() {
            ferramentasEspeciaisData.splice(idx, 1);
            redesenharTabelaFerramentas();
            guardarEstadoFormulario();
        };
        cell.appendChild(btn);
    });
}
