/**
 * Handles file selection for image previews.
 * @param {Event} e The change event.
 * @param {string} previewContainerId The ID of the container for image previews.
 */
window.handleFileSelect = function(event, previewContainerId) {
    const previewContainer = document.getElementById(previewContainerId);
    const files = event.target.files;
    if (!previewContainer) return;

    previewContainer.innerHTML = ''; // Limpa a pré-visualização anterior

    if (files && files.length > 0) {
        Array.from(files).forEach(file => {
            if (file.type.startsWith('image/')) {
                const reader = new FileReader();
                reader.onload = function(e) {
                    const img = document.createElement('img');
                    img.src = e.target.result; // O resultado do reader já é Base64
                    img.style.maxWidth = '150px';
                    img.style.maxHeight = '150px';
                    img.style.margin = '5px';
                    previewContainer.appendChild(img);
                };
                reader.readAsDataURL(file);
            }
        });
    }
}
/**
 * VERSÃO DEFINITIVA: Função unificada para gerir TODOS os uploads de imagem.
 * Converte todos os ficheiros para Base64 e guarda no objeto 'imagensCarregadasParaPDF'.
 * @param {Event} event - O evento do input de ficheiro.
 */
window.gerirUploadDeImagem = async function(event) {
    const input = event.target;
    const files = input.files;
    const inputId = input.id;

    if (!files || files.length === 0) return;

    // Lista de todas as secções que usam legendas
    const idsComLegenda = ['fotos_aspeto_visual', 'fotos_seguranca_ambiente', 'fotos_verificacao_entrega', 'anexar_diversos_files', 'fotos_gerais_stock_pecas', 'fotos_caixas_pecas', 'fotos_caixas_oficina', 'rentabilidade_imagem_upload'];
    const idVisita = document.getElementById('id_visita').value || `sem_id_${Date.now()}`;
    const statusDiv = document.getElementById('save-status');
    statusDiv.textContent = 'A carregar imagens...';
    statusDiv.style.display = 'block';

    if (!imagensCarregadasParaPDF[inputId]) {
        imagensCarregadasParaPDF[inputId] = [];
    }

    const uploadPromises = Array.from(files).map(file => {
        if (file.type.startsWith('image/')) {
            const storageRef = ref(storage, `auditorias/${idVisita}/${inputId}_${Date.now()}_${file.name}`);
            return uploadBytes(storageRef, file).then(snapshot => getDownloadURL(snapshot.ref));
        }
        return Promise.resolve(null);
    });

    try {
        const urls = await Promise.all(uploadPromises);
        const validUrls = urls.filter(url => url !== null);

        validUrls.forEach(url => {
            // Se o inputId for um dos que precisa de legenda, guarda como objeto
            if (idsComLegenda.includes(inputId)) {
                imagensCarregadasParaPDF[inputId].push({ url: url, legenda: '' });
            } else {
                imagensCarregadasParaPDF[inputId].push(url);
            }
        });

         redesenharPreviews(inputId);

        // --- CORREÇÃO AQUI ---
        // Salva os dados na cloud usando a nova lógica.
        // O `inputId` é o nome do campo a ser guardado.
        const docRef = doc(db, 'auditorias', idAuditoriaAtual);
        await updateDoc(docRef, {
            [inputId]: imagensCarregadasParaPDF[inputId]
        });
        // --- FIM DA CORREÇÃO ---

        mostrarStatusDeSave('Imagens carregadas ✓');
        formularioAlterado = true;

    } catch (error) {
        console.error("Erro no upload para o Storage:", error);
        mostrarStatusDeSave('Erro no upload!', 'erro');
    }
};
