// Função para atualizar a tabela de resumo 4.1.2
function updateVerificacaoTemposSummary() {
    const summaryTableBody = document.querySelector("#verificacao_tempos_summary_table tbody");
    const detailsTableBody = document.querySelector("#verificacao_tempos_details_table tbody");

    // A tabela de resumo deve ter sempre uma linha. Se não tiver, cria-a.
    if (summaryTableBody.rows.length === 0) {
        const newRow = summaryTableBody.insertRow();
        newRow.innerHTML = `
            <td><input type="date" id="summary_data_412" name="summary_data_412"></td>
            <td><input type="text" id="summary_num_viaturas_412" name="summary_num_viaturas_412" readonly></td>
            <td><input type="text" id="summary_ok_perc_412" name="summary_ok_perc_412" readonly></td>
            <td><input type="text" id="summary_nok_perc_412" name="summary_nok_perc_412" readonly></td>
        `;
    }
    const summaryRow = summaryTableBody.rows[0];

    const today = new Date();
    const year = today.getFullYear();
    const month = (today.getMonth() + 1).toString().padStart(2, '0');
    const day = today.getDate().toString().padStart(2, '0');
    const currentDate = `${year}-${month}-${day}`;

    let totalViaturasDetails = 0;
    let viaturasOkDetails = 0;
    let viaturasNokDetails = 0;

    Array.from(detailsTableBody.rows).forEach(row => {
        totalViaturasDetails++;
        const estadoSelect = row.cells[4].querySelector('select'); // Assumindo que 'estado' é a 5ª célula (índice 4)
        if (estadoSelect) {
            const estadoValue = estadoSelect.value.toLowerCase().trim();
            if (estadoValue === 'ok') {
                viaturasOkDetails++;
            } else if (estadoValue === 'nok') {
                viaturasNokDetails++;
            }
        }
    });

    const percentViaturasOkDetails = totalViaturasDetails > 0 ? ((viaturasOkDetails / totalViaturasDetails) * 100).toFixed(2) + '%' : '0.00%';
    const percentViaturasNokDetails = totalViaturasDetails > 0 ? ((viaturasNokDetails / totalViaturasDetails) * 100).toFixed(2) + '%' : '0.00%';

    // Preenche a linha de resumo
    summaryRow.cells[0].querySelector('input').value = currentDate;
    summaryRow.cells[1].querySelector('input').value = totalViaturasDetails;
    summaryRow.cells[2].querySelector('input').value = percentViaturasOkDetails;
    summaryRow.cells[3].querySelector('input').value = percentViaturasNokDetails;
}

