// NOVA FUNÇÃO para importar os dados da tabela de Penalizações
function importarPenalizacoes(event) {
    const file = event.target.files[0];
    if (!file) return;

    handleFileUpload(event); // Mantém o registo do nome do ficheiro
    const container = document.getElementById('penalizacoes_tabela_container');
    container.innerHTML = ''; // Limpa qualquer tabela anterior

    const reader = new FileReader();
    reader.onload = function(e) {
        try {
            const data = new Uint8Array(e.target.result);
            const workbook = XLSX.read(data, { type: 'array' });
            const worksheet = workbook.Sheets[workbook.SheetNames[0]]; // Pega na primeira folha
            const json = XLSX.utils.sheet_to_json(worksheet, { header: 1 });

            if (json.length === 0) return;

            const table = document.createElement('table');
            table.className = 'audit-table';
            const thead = table.createTHead();
            const tbody = table.createTBody();

            // Cabeçalho: Lê a primeira linha do Excel e cria os cabeçalhos
            const headerRow = thead.insertRow();
            const headers = json[0];
            headers.forEach(text => {
                const th = document.createElement('th');
                th.textContent = text;
                headerRow.appendChild(th);
            });

            // Corpo da Tabela: Percorre o resto das linhas
            for (let i = 1; i < json.length; i++) {
                const rowData = json[i];
                // Ignora linhas completamente vazias
                if (!rowData || rowData.every(cell => cell === null || cell === undefined)) continue;

                const row = tbody.insertRow();
                rowData.forEach(cellData => {
                    row.insertCell().textContent = cellData || '';
                });
            }

            container.appendChild(table);
            alert("Tabela de penalizações importada com sucesso!");
        } catch (error) {
            console.error("Erro ao importar tabela de penalizações:", error);
            alert("Ocorreu um erro ao ler o ficheiro Excel.");
        }
    };
    reader.readAsArrayBuffer(file);
}
/**
 * Reads an Excel file (XLSX) and stores the data in excelStockPecasData global variable.
 * Assumes the data is in the "Análise" sheet, cells as specified by the user.
 * @param {Event} event The change event from the file input.
 */
window.importarXlsStockPecas = function(event) {
    const file = event.target.files[0];
    const summaryBox = document.getElementById('stock_pecas_summary');
    
    if (!file) {
        excelStockPecasData = null;
        if (summaryBox) summaryBox.style.display = 'none';
        return;
    }

    displayFileName(event.target, 'filename_stock_pecas_display');
    const reader = new FileReader();
    reader.onload = (e) => {
        const data = new Uint8Array(e.target.result);
        try {
            const workbook = XLSX.read(data, { type: 'array' });
            const worksheet = workbook.Sheets["Análise"];
            if (!worksheet) {
                alert(`Folha "Análise" não encontrada.`);
                if (summaryBox) summaryBox.style.display = 'none';
                return;
            }

            const getCellValue = (addr) => {
                const cell = worksheet[addr];
                let value = cell ? cell.v : 0; // Default to 0 if cell is empty
                if (typeof value === 'string') {
                    // Tenta converter para número, se falhar mantém como string (para as percentagens)
                    const numValue = parseFloat(value.replace(/[^0-9,-.]+/g, "").replace(',', '.'));
                    value = isNaN(numValue) ? value : numValue;
                }
                return value;
            };

            excelStockPecasData = {
                // Linha 4 (Referências)
                ref_stock: getCellValue('C4'),
                ref_encontradas: getCellValue('D4'),
                ref_parcialmente_encontradas_parte_encontrada: getCellValue('E4'),
                ref_parcialmente_encontradas_parte_nao_encontrada: getCellValue('F4'),
                ref_nao_encontradas: getCellValue('G4'),
                ref_localizacoes_deficientes: getCellValue('I4'),
                // Linha 5 (Nº Peças)
                num_pecas_stock: getCellValue('C5'),
                num_pecas_encontradas: getCellValue('D5'),
                num_pecas_parcialmente_encontradas_parte_encontrada: getCellValue('E5'),
                num_pecas_parcialmente_encontradas_parte_nao_encontrada: getCellValue('F5'),
                num_pecas_nao_encontradas: getCellValue('G5'),
                num_pecas_localizacoes_deficientes: getCellValue('I5'),
                // Linha 6 (Valor)
                valor_stock: getCellValue('C6'),
                valor_encontradas: getCellValue('D6'),
                valor_parcialmente_encontradas_parte_encontrada: getCellValue('E6'),
                valor_parcialmente_encontradas_parte_nao_encontrada: getCellValue('F6'),
                valor_nao_encontradas: getCellValue('G6'),
                valor_localizacoes_deficientes: getCellValue('I6'),
                // Linha 8 (Percentagens)
                perc_encontradas: getCellValue('D8'),
                perc_parcialmente_encontradas: getCellValue('E8'),
                perc_nao_encontradas: getCellValue('G8')
                
            };

            // Atualizar o resumo na UI
            document.getElementById('ref_stock_summary').textContent = excelStockPecasData.ref_stock;
            document.getElementById('valor_stock_summary').textContent = excelStockPecasData.valor_stock.toFixed(2) + ' €';
            document.getElementById('num_pecas_parcial_enc_summary').textContent = excelStockPecasData.num_pecas_parcialmente_encontradas_parte_encontrada;
            document.getElementById('valor_parcial_enc_summary').textContent = excelStockPecasData.valor_parcialmente_encontradas_parte_encontrada.toFixed(2) + ' €';
            document.getElementById('num_pecas_parcial_nao_enc_summary').textContent = excelStockPecasData.num_pecas_parcialmente_encontradas_parte_nao_encontrada;
            document.getElementById('valor_parcial_nao_enc_summary').textContent = excelStockPecasData.valor_parcialmente_encontradas_parte_nao_encontrada.toFixed(2) + ' €';
            document.getElementById('ref_nao_encontradas_summary').textContent = excelStockPecasData.ref_nao_encontradas;
            document.getElementById('valor_nao_encontrado_summary').textContent = excelStockPecasData.valor_nao_encontradas.toFixed(2) + ' €';
            document.getElementById('loc_deficientes_summary').textContent = excelStockPecasData.ref_localizacoes_deficientes;
            
            if (summaryBox) summaryBox.style.display = 'block';

        } catch (error) {
            console.error("Erro ao processar ficheiro de Stock de Peças:", error);
            alert("Erro ao ler o ficheiro Excel.");
            excelStockPecasData = null;
            if (summaryBox) summaryBox.style.display = 'none';
        }
    };
    reader.readAsArrayBuffer(file);
}
