// NEW: Função para importar dados do Excel para 4.1.3 Ordens de Reparação em Curso
function importarOrsEmCurso(event) {
    const file = event.target.files[0];
    if (!file) {
        document.getElementById('filename_anexo_ors_em_curso_display').textContent = "Nenhum ficheiro selecionado.";
        document.getElementById('num_ors_em_curso').value = '';
        document.getElementById('valor_em_curso').value = '';
        ordensReparacaoEmCursoData = { numORs: '', valorEmCurso: '' };
        return;
    }

    document.getElementById('filename_anexo_ors_em_curso_display').textContent = file.name;
    const reader = new FileReader();

    reader.onload = function(e) {
        const data = new Uint8Array(e.target.result);
        const workbook = XLSX.read(data, { type: 'array' });

        const sheetName = 'em curso'; // Nome da folha conforme solicitado
        const worksheet = workbook.Sheets[sheetName];

        if (!worksheet) {
            alert(`A folha '${sheetName}' não foi encontrada no ficheiro Excel para OR's em Curso.`);
            document.getElementById('num_ors_em_curso').value = '';
            document.getElementById('valor_em_curso').value = '';
            ordensReparacaoEmCursoData = { numORs: '', valorEmCurso: '' };
            return;
        }

        // Ler todos os dados da folha para fácil acesso
        const jsonData = XLSX.utils.sheet_to_json(worksheet, { header: 1 });

        let numORs = 0;
        let valorEmCurso = 0;

        // Assumimos que a primeira linha é o cabeçalho, então começamos da segunda linha (índice 1)
        // A coluna K é o índice 10 (A=0, B=1, ..., K=10)
        if (jsonData.length > 1) { // Verifica se há mais do que apenas o cabeçalho
            for (let i = 1; i < jsonData.length; i++) {
                const row = jsonData[i];
                const valorK = parseFloat(String(row[10]).replace(',', '.')); // Handle comma as decimal separator

                if (!isNaN(valorK)) {
                    numORs++;
                    valorEmCurso += valorK;
                }
            }
        }

        document.getElementById('num_ors_em_curso').value = numORs;
        document.getElementById('valor_em_curso').value = valorEmCurso.toFixed(2); // Formatar para 2 casas decimais

        ordensReparacaoEmCursoData = {
            numORs: numORs,
            valorEmCurso: valorEmCurso.toFixed(2)
        };
        console.log(`OR's em Curso - Nº OR's: ${numORs}, Valor em Curso: ${valorEmCurso.toFixed(2)}€`);
    };
    reader.readAsArrayBuffer(file);
    guardarEstadoFormulario();
}

