// Esta é a sua função que processa o ficheiro
function importarControloOrsFechadas(event) {
    const file = event.target.files[0];
    const btnGravar = document.getElementById('btnGravarControloORs');

    if (!file) {
        document.getElementById('filename_anexo_controlo_ors_fechadas_display').textContent = "Nenhum ficheiro selecionado.";
        document.querySelector('[name="ors_solicitadas"]').value = '';
        document.querySelector('[name="ors_ok"]').value = '';
        document.querySelector('[name="ors_nok"]').value = '';
        document.querySelector('[name="ors_nao_apresentadas"]').value = '';
        btnGravar.style.display = 'none';
        guardarEstadoFormulario(); // Para limpar o estado
        return;
    }

    document.getElementById('filename_anexo_controlo_ors_fechadas_display').textContent = file.name;
    const reader = new FileReader();

    reader.onload = function(e) {
        const data = new Uint8Array(e.target.result);
        const workbook = XLSX.read(data, { type: 'array' });
        const sheetName = 'controlo'; 
        const worksheet = workbook.Sheets[sheetName];

        if (!worksheet) {
            alert(`A folha '${sheetName}' não foi encontrada no ficheiro Excel para Controlo Processual de OR's Fechadas.`);
            document.querySelector('[name="ors_solicitadas"]').value = '';
            document.querySelector('[name="ors_ok"]').value = '';
            document.querySelector('[name="ors_nok"]').value = '';
            document.querySelector('[name="ors_nao_apresentadas"]').value = '';
            btnGravar.style.display = 'none';
            return;
        }

        const jsonData = XLSX.utils.sheet_to_json(worksheet, { header: 1 });
        let orsSolicitadas = 0;
        let orsOK = 0;
        let orsNOK = 0;
        let orsNaoApresentadas = 0;

        if (jsonData.length > 0) {
            for (let i = 0; i < jsonData.length; i++) {
                const row = jsonData[i];
                const valorAE = row[30] ? String(row[30]).trim().toLowerCase() : '';

                if (valorAE !== '') {
                    orsSolicitadas++;
                }
                if (valorAE === 'ok') {
                    orsOK++;
                } else if (valorAE === 'nok') {
                    orsNOK++;
                } else if (valorAE === 'não apresentada' || valorAE === 'nao apresentada') {
                    orsNaoApresentadas++;
                }
            }
        }

        // Atribui os valores aos campos do HTML
        document.querySelector('[name="ors_solicitadas"]').value = orsSolicitadas;
        document.querySelector('[name="ors_ok"]').value = orsOK;
        document.querySelector('[name="ors_nok"]').value = orsNOK;
        document.querySelector('[name="ors_nao_apresentadas"]').value = orsNaoApresentadas;

        // **PASSO CRUCIAL:** Dispara um evento de 'input' para simular uma alteração manual EM TODOS OS CAMPOS.
        document.querySelector('[name="ors_solicitadas"]').dispatchEvent(new Event('input', { bubbles: true }));
        document.querySelector('[name="ors_ok"]').dispatchEvent(new Event('input', { bubbles: true }));
        document.querySelector('[name="ors_nok"]').dispatchEvent(new Event('input', { bubbles: true }));
        document.querySelector('[name="ors_nao_apresentadas"]').dispatchEvent(new Event('input', { bubbles: true }));

        alert('Dados importados com sucesso!');
    };

    reader.readAsArrayBuffer(file);
}

