            '4.1.2 Verificação Tempos': () => document.querySelector("#verificacao_tempos_details_table tbody").rows.length > 0,
            '4.1.3 ORs em Curso': () => document.getElementById('total_num_ors_curso').value.trim() !== '',
            '4.2.1 Rentabilidade': () => document.getElementById('rentabilidade_comentarios').value.trim() !== '',
