    const addImageToPDF = async (inputId, title) => {
    const imagensArray = dadosParaPDF.imagensCarregadasParaPDF[inputId];
    if (!imagensArray || imagensArray.length === 0) return;

    yPos = checkPageBreak(yPos + 5);
    pdfDoc.setFontSize(10).setFont(undefined, 'bold');
    pdfDoc.text(title, margin, yPos);
    yPos += 5;

    for (const imgData of imagensArray) {
        const imageUrl = typeof imgData === 'object' ? imgData.url : imgData;
        if (!imageUrl) continue;

        try {
            const response = await fetch(imageUrl);
            const blob = await response.blob();
            const base64String = await new Promise((resolve, reject) => {
                const reader = new FileReader();
                reader.onloadend = () => resolve(reader.result);
                reader.onerror = reject;
                reader.readAsDataURL(blob);
            });

            const props = pdfDoc.getImageProperties(base64String);
            const ratio = props.width / props.height;
            let imgWidth = 160;
            let imgHeight = imgWidth / ratio;
            if (imgHeight > 110) { imgHeight = 110; imgWidth = imgHeight * ratio; }

            // --- INÍCIO DA LÓGICA DE POSICIONAMENTO CORRIGIDA ---

            // 1. Verifica se a imagem + legenda cabem na página atual
            const alturaLegenda = (typeof imgData === 'object' && imgData.legenda) ? 8 : 0;
            if (yPos + imgHeight + alturaLegenda > pageHeight - 25) { // 25 é a margem de segurança
                addFooter();
                pdfDoc.addPage();
                addHeader();
                yPos = margin + 15; // Posição inicial na nova página
            }

            // 2. Desenha a imagem na posição yPos atual (que agora está correta)
            pdfDoc.addImage(base64String, props.fileType.toUpperCase(), margin, yPos, imgWidth, imgHeight);

            // 3. Avança a posição para DEBAIXO da imagem
            yPos += imgHeight + 2;

            // 4. Desenha a legenda (se existir) e avança a posição
            if (typeof imgData === 'object' && imgData.legenda) {
                pdfDoc.setFontSize(8).setFont(undefined, 'italic');
                pdfDoc.setTextColor(80, 80, 80);
                const maxWidth = pageWidth - margin * 2;
                // Divide a legenda em várias linhas se for demasiado longa
                const textLines = pdfDoc.splitTextToSize(imgData.legenda, maxWidth);
                const textHeight = textLines.length * (8 * 0.35); // Calcula a altura do texto

                yPos = checkPageBreak(yPos + textHeight); // Verifica se a legenda cabe
                pdfDoc.text(textLines, margin, yPos);
                yPos += textHeight + 2; // Avança a posição com a altura da legenda
                pdfDoc.setTextColor(0, 0, 0);
                yPos += 5; // Espaço extra depois da legenda
            } else {
                yPos += 3; // Espaço se não houver legenda
            }
            // --- FIM DA LÓGICA CORRIGIDA ---

        } catch (e) {
            console.error(`Erro ao processar a imagem ${imageUrl}:`, e);
            addText(`[Erro ao carregar imagem]`, margin, 8, 'italic');
        }
    }
};
    
