window.apagarAuditoriaAtual = async function() {
    // Verifica se há uma auditoria carregada para apagar
    if (!idAuditoriaAtual) {
        alert("Nenhuma auditoria está carregada. Não há nada para apagar.");
        return;
    }

    // Pede confirmação final ao utilizador para evitar acidentes
    const nomeRelatorio = document.getElementById('id_visita')?.value || 'atual';
    const confirmacao = confirm(`Tem a certeza de que deseja apagar permanentemente o relatório "${nomeRelatorio}"? Esta ação não pode ser desfeita.`);

    if (confirmacao) {
        console.log(`A apagar o documento com ID: ${idAuditoriaAtual}...`);
        mostrarStatusDeSave('A apagar...', '#dc3545');

        try {
            // Cria a referência para o documento e apaga-o da Firestore
            const docRef = doc(db, 'auditorias', idAuditoriaAtual);
            await deleteDoc(docRef);

            // Após apagar, limpa o formulário e o estado da aplicação
            limparFormularioSemRecarregar();
            idAuditoriaAtual = null;
            document.getElementById('current_audit_id').value = '';
            localStorage.removeItem('idUltimaAuditoria');
            
            mostrarStatusDeSave('Auditoria apagada com sucesso ✓', 'sucesso');
            alert("A auditoria foi apagada com sucesso.");

        } catch (error) {
            console.error("Erro ao apagar a auditoria:", error);
            mostrarStatusDeSave('Erro ao apagar!', 'erro');
            alert("Ocorreu um erro ao tentar apagar a auditoria. Por favor, tente novamente.");
        }
    } else {
        console.log("O utilizador cancelou a operação de apagar.");
    }
}
