window.salvarAuditoriaNaCloud = async function() {
    if (!podeSalvar) return;
    if (!formularioAlterado) {
        mostrarStatusDeSave('Nenhuma alteração para guardar.', 'info');
        return;
    }
    podeSalvar = false;

    // Obtém o ID da visita do formulário
    const idDaVisita = document.getElementById('id_visita').value;
    
    // Verifica se o ID da visita está preenchido antes de salvar
    if (!idDaVisita && !idAuditoriaAtual) {
        mostrarStatusDeSave('Por favor, preencha o ID da visita.', 'erro');
        podeSalvar = true;
        return;
    }
const docRef = doc(db, "auditorias", idDaVisita || idAuditoriaAtual);
    const dadosParaGuardar = montarDadosCompletosParaAcao();
    await setDoc(docRef, dadosParaGuardar, { merge: true });
    try {
        if (idAuditoriaAtual) {
            // Lógica de atualização para auditorias existentes (sem alterações)
            const docRef = doc(db, 'auditorias', idAuditoriaAtual);
            await updateDoc(docRef, dadosParaGuardar);
            mostrarStatusDeSave('Alterações guardadas na nuvem ✓', 'sucesso');
        } else {
            // --- CORREÇÃO AQUI ---
            // Usa o ID da visita do formulário para o nome do documento
            const docRef = doc(db, 'auditorias', idDaVisita);

            // Cria o documento com o ID e os dados do formulário
            await setDoc(docRef, {
                ...dadosParaGuardar,
                nomeAuditoria: idDaVisita, // Adiciona o nome da auditoria
                timestamp: serverTimestamp(),
                estado: 'Em Curso'
            });
            
            // Define o idAuditoriaAtual para o ID que acabamos de usar
            idAuditoriaAtual = idDaVisita; 
            document.getElementById('current_audit_id').value = idAuditoriaAtual;
            localStorage.setItem('idUltimaAuditoria', idAuditoriaAtual);
            setupRealtimeListener(idAuditoriaAtual);
            mostrarStatusDeSave('Auditoria criada e guardada na nuvem ✓', 'sucesso');
        }
        
        formularioAlterado = false;
    } catch (error) {
        console.error("Erro ao guardar na nuvem:", error);
        mostrarStatusDeSave('Erro ao guardar!', 'erro');
    } finally {
        podeSalvar = true;
    }
};

