let statusTimeout; // Variável para controlar o tempo do fade-out

function mostrarStatusDeSave(mensagem, tipo = 'sucesso') {
    const statusDiv = document.getElementById('save-status');
    if (!statusDiv) return;
    clearTimeout(statusTimeout);
    statusDiv.textContent = mensagem;
    statusDiv.style.backgroundColor = tipo === 'erro' ? '#d9534f' : '#5cb85c'; // Vermelho para erro, verde para sucesso
    statusDiv.style.opacity = 1; // Mostra a mensagem
    statusTimeout = setTimeout(() => {
    statusDiv.style.opacity = 0;
    }, 2500);
}

// VERSÃO CORRIGIDA (com filtro no lado do cliente)
window.mostrarListaDeAuditorias = async function() {
    const modal = document.getElementById('lista-auditorias-modal');
    const listContainer = document.getElementById('lista-de-auditorias');
    if (!modal || !listContainer) {
        console.error("ERRO: Elementos do modal de lista de auditorias não encontrados no HTML.");
        return;
    }

    listContainer.innerHTML = '<li>A carregar auditorias...</li>';
    modal.style.display = 'block';

    try {
        // 1. Pede TODAS as auditorias, ordenadas por data (isto usa o índice simples que já tem).
        const q = query(collection(db, "auditorias"), orderBy("timestamp", "desc"));
        const querySnapshot = await getDocs(q);
        
        listContainer.innerHTML = ''; // Limpa a mensagem "A carregar..."

        // 2. Filtra os resultados AQUI, no JavaScript, para remover o histórico.
        const auditoriasAtivas = querySnapshot.docs.filter(doc => doc.data().tipo !== 'historico');

        if (auditoriasAtivas.length === 0) {
            listContainer.innerHTML = '<li>Nenhuma auditoria ativa encontrada para carregar.</li>';
            return;
        }

        // 3. Para cada auditoria ativa, cria um botão claro na lista.
        auditoriasAtivas.forEach(doc => {
            const auditoria = doc.data();
            const listItem = document.createElement('li');
            
            const button = document.createElement('button');
            button.textContent = `${auditoria.id_visita || doc.id} - ${auditoria.data_auditoria || 'Sem data'}`;
            button.onclick = () => carregarAuditoriaSelecionada(doc.id);
            
            listItem.appendChild(button);
            listContainer.appendChild(listItem);
        });
    } catch (error) {
        console.error("Erro ao carregar lista de auditorias:", error);
        listContainer.innerHTML = '<li>Ocorreu um erro ao carregar a lista. Verifique a consola (F12).</li>';
    }
}

