function atualizarSelecaoLogos() {
    const oficinasSelect = document.getElementById('oficinas_auditadas');
    const logosSelect = document.getElementById('marca_logo_select');
    if (!oficinasSelect || !logosSelect) return;

    // 1. Obtém as marcas das oficinas selecionadas, convertendo para minúsculas
    const marcasSelecionadas = new Set();
    const oficinasSelecionadas = Array.from(oficinasSelect.selectedOptions).map(opt => opt.value);

    oficinasSelecionadas.forEach(oficinaTexto => {
        const partes = oficinaTexto.split(' ');
        if (partes.length > 1) {
            const marca = partes[1].toLowerCase(); // Ex: "opel"
            marcasSelecionadas.add(marca);
        }
    });

    // 2. Itera por todas as opções na lista de logótipos
    Array.from(logosSelect.options).forEach(option => {
        // Ignora a primeira opção se for vazia (ex: "-- Sem Logo --")
        if (option.value === "") {
            option.selected = false;
            return;
        }

        const optionValueLower = option.value.toLowerCase(); // Ex: "opel"

        // 3. Seleciona a opção se a sua marca corresponder a uma das marcas selecionadas
        if (marcasSelecionadas.has(optionValueLower)) {
            option.selected = true;
        } else {
            option.selected = false;
        }
    });
}

window.processarORsEmCurso = function(event) {
    const file = event.target.files[0];
    const numOrsInput = document.getElementById('total_num_ors_curso');
    const valorOrsInput = document.getElementById('total_valor_ors_curso');

    if (!file) {
        // Limpa os campos se nenhum ficheiro for selecionado
        if(numOrsInput) numOrsInput.value = '';
        if(valorOrsInput) valorOrsInput.value = '';
        return;
    }

    displayFileName(event.target, 'filename_anexo_ors_em_curso_display');
    const reader = new FileReader();

    reader.onload = function(e) {
        try {
            const data = new Uint8Array(e.target.result);
            const workbook = XLSX.read(data, { type: 'array' });
            const sheetName = "Em curso";
            if (!workbook.SheetNames.includes(sheetName)) {
                alert(`Erro: A folha "${sheetName}" não foi encontrada no ficheiro Excel.`);
                return;
            }
            const worksheet = workbook.Sheets[sheetName]; 
            const json = XLSX.utils.sheet_to_json(worksheet, { header: 1 });

            const columnIndex = 10; // Coluna K (A=0, B=1, ..., K=10)
            
            let valorCount = 0;
            let valorSum = 0;

            // Começa na segunda linha (índice 1) para ignorar o cabeçalho
            for (let i = 1; i < json.length; i++) {
                const row = json[i];
                if (row && row[columnIndex] !== undefined && row[columnIndex] !== null) {
                    const cellValue = parseFloat(String(row[columnIndex]).replace(',', '.'));
                    if (!isNaN(cellValue)) {
                        valorCount++;
                        valorSum += cellValue;
                    }
                }
            }

            // Preenche os campos na interface
            if(numOrsInput) numOrsInput.value = valorCount;
            if(valorOrsInput) valorOrsInput.value = `${valorSum.toFixed(2)} €`;

            guardarEstadoFormulario(); // <-- CHAMA AQUI, depois de preencher os inputs!

            alert('Ficheiro de ORs em curso processado com sucesso!');

        } catch (error) {
            console.error("Erro ao processar o ficheiro de ORs em curso:", error);
            alert("Ocorreu um erro ao ler o ficheiro Excel.");
        }
    };
    reader.readAsArrayBuffer(file);
}
window.processarControloORs = function(event) {
    const file = event.target.files[0];
    if (!file) return;

    displayFileName(event.target, 'filename_anexo_controlo_ors_fechadas_display');
    const reader = new FileReader();
    reader.onload = function(e) {
        try {
            const data = new Uint8Array(e.target.result);
            const workbook = XLSX.read(data, { type: 'array' });
            // Assumimos que os dados estão na primeira folha
            const worksheet = workbook.Sheets[workbook.SheetNames[0]]; 
            const json = XLSX.utils.sheet_to_json(worksheet, { header: 1 });

            // A coluna AE é a 31ª coluna, que tem o índice 30
            const columnIndex = 30; 
            
            let totalCount = 0;
            let okCount = 0;
            let nokCount = 0;

            // Começa na segunda linha para ignorar o cabeçalho
            for (let i = 1; i < json.length; i++) {
                const cellValue = json[i][columnIndex];
                if (cellValue !== undefined && cellValue !== null && cellValue !== "") {
                    totalCount++;
                    const upperCaseValue = String(cellValue).trim().toUpperCase();
                    if (upperCaseValue === 'OK') {
                        okCount++;
                    } else if (upperCaseValue === 'NOK') {
                        nokCount++;
                    }
                }
            }

            const naoApresentadas = totalCount - (okCount + nokCount);

            // Preenche os campos da tabela
            document.querySelector('#controlo_ors_fechadas_table input[name="ors_solicitadas"]').value = totalCount;
            document.querySelector('#controlo_ors_fechadas_table input[name="ors_ok"]').value = okCount;
            document.querySelector('#controlo_ors_fechadas_table input[name="ors_nok"]').value = nokCount;
            document.querySelector('#controlo_ors_fechadas_table input[name="ors_nao_apresentadas"]').value = naoApresentadas;

            alert('Ficheiro de controlo de ORs processado com sucesso!');
        } catch (error) {
            console.error("Erro ao processar o ficheiro de controlo de ORs:", error);
            alert("Ocorreu um erro ao ler o ficheiro Excel.");
        }
    };
    reader.readAsArrayBuffer(file);
}

