// Main handleFileUpload function
window.handleFileUpload = function(event) {
    const input = event.target;
    const files = input.files;
    const inputId = input.id;

    // Garante que o array para este input existe
    if (!imagensCarregadasParaPDF[inputId]) {
        imagensCarregadasParaPDF[inputId] = [];
    }
    
    // Limpa os dados antigos para este input específico
    imagensCarregadasParaPDF[inputId] = [];

    if (!files || files.length === 0) {
        formularioAlterado = true;
        return;
    }

    const filePromises = Array.from(files).map(file => {
        return new Promise((resolve) => {
            // Verifica se é uma imagem
            if (file.type.startsWith('image/')) {
                const reader = new FileReader();
                reader.onload = function(e) {
                    // Adiciona a string Base64 ao nosso objeto de dados
                    imagensCarregadasParaPDF[inputId].push(e.target.result);
                    resolve(); // Avisa que esta imagem terminou
                };
                reader.onerror = () => resolve(); // Continua mesmo se uma imagem falhar
                reader.readAsDataURL(file);
            } else {
                // Se não for uma imagem (ex: Excel), não faz nada aqui
                resolve();
            }
        });
    });

    Promise.all(filePromises).then(() => {
        console.log(`Dados de ficheiros para '${inputId}' preparados para guardar.`);
        formularioAlterado = true;
    });
};
