window.processarAcompanhamentoPlanoAnterior = async function(event) {
    const file = event.target.files[0];
    if (!file) {
        acompanhamentoPlanoAnteriorDataGlobal = [];
        const container = document.getElementById('acompanhamento_tabela_container');
        if (container) {
            container.innerHTML = '';
        }
        await guardarEstadoFormulario();
        return;
    }

    handleFileUpload(event); 

    const reader = new FileReader();
    const fileExtension = file.name.split('.').pop().toLowerCase();
    
    reader.onload = async function(e) {
        try {
            let workbook;
            if (fileExtension === 'csv') {
                const csvString = e.target.result;
                workbook = XLSX.read(csvString, { type: 'binary' });
            } else { // Presume que é xlsx, xls
                const data = new Uint8Array(e.target.result);
                workbook = XLSX.read(data, { type: 'array' });
            }

            // Seleciona a primeira folha, que é a que interessa, independentemente do tipo de ficheiro
            const firstSheetName = workbook.SheetNames[0];
            const worksheet = workbook.Sheets[firstSheetName];

            if (!worksheet) {
                throw new Error("A folha de trabalho não foi encontrada no ficheiro.");
            }

            const json = XLSX.utils.sheet_to_json(worksheet, { header: 1 });
            
            if (json.length < 2) {
                throw new Error("O ficheiro não contém dados válidos.");
            }
            
            const headers = json[0].map(h => h.trim());
            const dataRows = json.slice(1);
            
            const situacaoIndex = headers.indexOf("Situação a corrigir");
            const responsavelIndex = headers.indexOf("Responsável pela ação");
            const medidaIndex = headers.indexOf("Medida corretiva");
            const dataLimiteIndex = headers.indexOf("Data limite");

            if (situacaoIndex === -1 || responsavelIndex === -1 || medidaIndex === -1 || dataLimiteIndex === -1) {
                throw new Error("O ficheiro Excel não tem as colunas necessárias. Por favor, verifique os nomes: Situação a corrigir, Responsável pela ação, Medida corretiva, Data limite.");
            }

            acompanhamentoPlanoAnteriorDataGlobal = [];
            dataRows.forEach(row => {
                acompanhamentoPlanoAnteriorDataGlobal.push({
                    situacao: row[situacaoIndex] || '',
                    responsavel: row[responsavelIndex] || '',
                    medida: row[medidaIndex] || '',
                    data_limite: row[dataLimiteIndex] || '',
                    status: '', 
                    observacoes: ''
                });
            });

            console.log("Dados lidos do Excel para a variável global:", acompanhamentoPlanoAnteriorDataGlobal);

            preencherAcompanhamentoPlanoAnteriorTabela();
            await guardarEstadoFormulario();
            
            alert('Acompanhamento do plano de ação anterior importado e preenchido com sucesso!');

        } catch (error) {
            console.error("Erro ao processar o ficheiro do Acompanhamento do Plano de Ação:", error);
            alert(`Ocorreu um erro ao ler o ficheiro Excel: ${error.message}`);
        }
    };
    
    if (fileExtension === 'csv') {
        reader.readAsBinaryString(file);
    } else {
        reader.readAsArrayBuffer(file);
    }
}

// Crie esta nova função para renderizar a tabela
function preencherAcompanhamentoPlanoAnteriorTabela() {
    const container = document.getElementById('acompanhamento_tabela_container');
    if (!container) return;
    container.innerHTML = '';
    const table = document.createElement('table');
    table.className = 'audit-table';
    table.id = 'tabela_acompanhamento_importada';
    const thead = table.createTHead();
    const tbody = table.createTBody();
    const headerRow = thead.insertRow();
    const headers = ["Situação a corrigir", "Responsável", "Medida corretiva", "Data limite", "Acompanhamento", "Observações"];
    headers.forEach(text => {
        const th = document.createElement('th');
        th.textContent = text;
        headerRow.appendChild(th);
    });

    acompanhamentoPlanoAnteriorDataGlobal.forEach((rowData, i) => {
        const row = tbody.insertRow();
        row.insertCell(0).textContent = rowData.situacao || '';
        row.insertCell(1).textContent = rowData.responsavel || '';
        row.insertCell(2).textContent = rowData.medida || '';
        row.insertCell(3).textContent = rowData.data_limite || '';
        const cellSelect = row.insertCell(4);
        const select = document.createElement('select');
        select.name = `acompanhamento_status_${i}`;
        select.className = "form-control";
        const options = ["Correção efetuada", "Parcialmente corrigido", "Não implementado"];
        const emojis = ["😊", "😐", "😞"];
        options.forEach((opt, index) => {
            const option = document.createElement('option');
            option.value = opt;
            option.textContent = `${emojis[index]} ${opt}`;
            if (rowData.status === opt) {
                option.selected = true;
            }
            select.appendChild(option);
        });
        select.value = rowData.status || '';
        // CORREÇÃO: Adiciona um listener para atualizar a variável global e salvar
        select.addEventListener('change', (e) => {
            acompanhamentoPlanoAnteriorDataGlobal[i].status = e.target.value;
            guardarEstadoFormulario(e);
        });
        cellSelect.appendChild(select);
        const cellObs = row.insertCell(5);
        const textarea = document.createElement('textarea');
        textarea.rows = "2";
        textarea.className = "form-control";
        textarea.name = `acompanhamento_obs_${i}`;
        textarea.placeholder = "Adicionar observação...";
        textarea.value = rowData.observacoes || '';
        // CORREÇÃO: Adiciona um listener para atualizar a variável global e salvar
        textarea.addEventListener('input', (e) => {
            acompanhamentoPlanoAnteriorDataGlobal[i].observacoes = e.target.value;
            guardarEstadoFormulario(e);
        });
        cellObs.appendChild(textarea);
    });
    container.appendChild(table);
}

