/**
 * Atualiza dinamicamente a tabela do Plano de Ação.
 * Adiciona, atualiza ou remove uma linha na tabela com base no conteúdo
 * da caixa de "Considerações Gerais" que foi alterada.
 * @param {Event} event - O evento 'input' disparado pela textarea.
 */
function atualizarPlanoDeAcao(event) {
    const sourceId = event.target.id; // Ex: 'consideracoes_aspeto_visual'
    const text = event.target.value.trim();
    const planoAcaoTableBody = document.querySelector("#plano_acao_table tbody");

    // Procura por uma linha que já corresponda a esta fonte de texto
    let targetRow = planoAcaoTableBody.querySelector(`tr[data-source-id="${sourceId}"]`);

    if (text !== '') {
        // Se há texto, a linha deve existir ou ser criada
        if (!targetRow) {
            // Se não existe, cria uma nova
            targetRow = adicionarLinhaPlanoAcao(); // Usa a função que já existe para criar a estrutura da linha
            targetRow.setAttribute('data-source-id', sourceId); // Adiciona o identificador
        }
        // Atualiza o conteúdo da primeira célula
        const situacaoTextarea = targetRow.cells[0].querySelector('textarea');
        if (situacaoTextarea) {
            situacaoTextarea.value = text;
        }
    } else {
        // Se o texto foi apagado e a linha existe, remove a linha
        if (targetRow) {
            targetRow.remove();
        }
    }
}

