window.gravarPenalizacao = function() {
    const radioSelecionado = document.querySelector('input[name="penalizacaoSelecionada"]:checked');
    if (!radioSelecionado) {
        alert("Por favor, selecione uma penalização.");
        return;
    }

    const dadosPenalizacao = JSON.parse(radioSelecionado.value);
    const artigoId = dadosPenalizacao.artigo;
    const colaboradorSelecionado = document.getElementById('colaborador-select').value;
    const quantidade = parseInt(document.getElementById('quantidade-penalizacao').value) || 1;

    if (!colaboradorSelecionado) {
        alert("Por favor, selecione o colaborador a penalizar.");
        return;
    }

    // Cria a chave única e consistente
    const chaveUnica = `${artigoId}-${colaboradorSelecionado.replace(/\s+/g, '_')}`;

    let valorAplicadoNum = 0;
    if (dadosPenalizacao.valorVariavel) {
        const valorManualInput = document.getElementById('valor-manual-input');
        if (!valorManualInput.value || parseFloat(valorManualInput.value) <= 0) {
            alert("Para esta penalização, é obrigatório inserir um valor a aplicar.");
            return;
        }
        valorAplicadoNum = parseFloat(valorManualInput.value);
    } else {
        valorAplicadoNum = parseFloat(dadosPenalizacao.valor.replace('€', '').replace(',', '.').trim()) || 0;
    }

    // Guarda a penalização usando a chave correta
    penalizacoesAplicadas[chaveUnica] = {
        dados: dadosPenalizacao,
        valorAplicado: valorAplicadoNum,
        colaborador: colaboradorSelecionado,
        quantidade: quantidade,
        comentarios: '' // Pode adicionar um campo para comentários se desejar
    };
    
    alert(`Penalização do Artigo ${artigoId} gravada para ${colaboradorSelecionado}.`);
    window.fecharModalPenalizacoes();
    atualizarResumoPenalizacoesHTML();
    formularioAlterado = true;
}

window.exportarPenalizacoesAplicadasParaExcel = function() {
    const penalizacoes = Object.values(penalizacoesAplicadas);
    if (penalizacoes.length === 0) { /*...*/ return; }

    let csv = [];
    const headers = ['Artigo', 'Descrição', 'Comentários', 'Valor Aplicado', 'Código Colaborador', 'Nome Colaborador'];
    csv.push(headers.join(';'));

    penalizacoes.forEach(item => {
        const pen = item.dados;
        const [codigoColaborador, nomeColaborador] = item.colaborador ? item.colaborador.split(';') : ['', ''];

        const rowData = [
            `"${pen.artigo}"`,
            `"${pen.descricao.replace(/"/g, '""')}"`,
            `"${(item.comentarios || '').replace(/"/g, '""')}"`,
            `"${item.valorAplicado.toFixed(2)}"`, // Usa o valor numérico
            `"${codigoColaborador}"`,
            `"${nomeColaborador || ''}"`
        ];
        csv.push(rowData.join(';'));
    });
    
    // ... (resto da sua função de download do CSV) ...
    const csvString = csv.join('\n');
    const blob = new Blob(['\uFEFF' + csvString], { type: 'text/csv;charset=utf-8;' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `Resumo_Penalizacoes_${document.getElementById('id_visita')?.value || 'export'}.csv`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
}

