/**
 * Lê os dados da tabela editável e grava as respostas na base de dados.
 */
window.gravarRespostaPA = async function() {
    const botao = document.getElementById('btn-gravar-resposta-pa');
    const docId = botao.dataset.docId;
    const dataResposta = new Date().toLocaleDateString('pt-PT');

    if (confirm(`Confirma que deseja gravar e submeter estas respostas a ${dataResposta}?`)) {
        const docRef = doc(db, "auditorias", docId);

        // Lê os dados da auditoria para obter o plano de ação original
        const docSnap = await getDoc(docRef);
        if (!docSnap.exists()) {
            alert("Erro: A auditoria original não foi encontrada.");
            return;
        }
        const planoAcaoOriginal = docSnap.data().plano_acao_table || [];

        // Atualiza o plano de ação com as novas respostas
        const planoAcaoAtualizado = planoAcaoOriginal.map((item, index) => {
            const novaMedida = document.getElementById(`resposta-medida-${index}`).value;
            const novaData = document.getElementById(`resposta-data-${index}`).value;
            return {
                ...item,
                medida_corretiva: novaMedida,
                data_limite: novaData
            };
        });

        try {
            await updateDoc(docRef, {
                plano_acao_table: planoAcaoAtualizado,
                estadoPlanoAcao: "Respondido",
                data_resposta_pa: dataResposta
            });
            alert("Obrigado! A sua resposta foi gravada com sucesso.");
            botao.disabled = true;
            botao.textContent = "Resposta Gravada";
        } catch (error) {
            console.error("Erro ao gravar resposta:", error);
            alert("Ocorreu um erro ao gravar a sua resposta.");
        }
    }
}

