/**
 * VERSÃO ATUALIZADA: Introduz o novo estado "Acompanhamento com Pendentes".
 */
window.gravarAvaliacaoPA = async function() {
    const botao = document.getElementById('btn-gravar-avaliacao-pa');
    const docId = botao.dataset.docId;

    if (confirm("Confirma que deseja gravar esta avaliação?")) {
        const docRef = doc(db, "auditorias", docId);
        const docSnap = await getDoc(docRef);
        if (!docSnap.exists()) return alert("Erro: Auditoria não encontrada.");

        const planoAcaoOriginal = docSnap.data().plano_acao_table || [];
        let temPendentes = false; // Variável de controlo

        // Atualiza o plano de ação com a avaliação do auditor
        const planoAcaoAvaliado = planoAcaoOriginal.map((item, index) => {
            const estadoAcao = document.getElementById(`avaliacao-estado-${index}`).value;
            const obsAuditor = document.getElementById(`avaliacao-obs-${index}`).value;
            
            // Verifica se alguma ação ficou como "Não Implementado"
            if (estadoAcao === 'Não Implementado') {
                temPendentes = true;
            }

            return { ...item, estado_acao: estadoAcao, obs_auditor: obsAuditor };
        });

        // --- INÍCIO DA LÓGICA DO NOVO ESTADO ---
        // Decide qual o estado final com base na verificação
        const novoEstadoPA = temPendentes ? "Acompanhamento com Pendentes" : "Acompanhamento Concluído";
        // --- FIM DA LÓGICA ---

        try {
            await updateDoc(docRef, {
                plano_acao_table: planoAcaoAvaliado,
                estadoPlanoAcao: novoEstadoPA, // Usa o novo estado
                dataAcompanhamentoPA: new Date()
            });
            alert("Avaliação guardada com sucesso!");
            window.mostrarConsolaDeGestao(); // Volta para a consola
        } catch (error) {
            console.error("Erro ao gravar avaliação:", error);
            alert("Ocorreu um erro ao gravar a sua avaliação.");
        }
    }
}
