let unsubscribeNotificacoes = null;

function setupNotificacoesListener(userId) {
    if (unsubscribeNotificacoes) unsubscribeNotificacoes(); // Cancela o ouvinte anterior

    const q = query(collection(db, "notificacoes"), where("para_auditor_id", "==", userId), where("lida", "==", false));

    unsubscribeNotificacoes = onSnapshot(q, (snapshot) => {
        const contador = document.getElementById('contador-notificacoes');
        const painel = document.getElementById('painel-notificacoes');

        if (snapshot.empty) {
            contador.classList.add('hidden');
            painel.innerHTML = '<p style="padding: 10px;">Nenhuma notificação nova.</p>';
        } else {
            contador.textContent = snapshot.size;
            contador.classList.remove('hidden');
            painel.innerHTML = '';
            snapshot.forEach(doc => {
                const notificacao = doc.data();
                const div = document.createElement('div');
                div.className = 'notificacao';
                div.innerHTML = `<p><strong>${notificacao.titulo}</strong><br>${notificacao.mensagem}</p>`;
                // Ao clicar, pode marcar como lida e navegar para a auditoria
                div.onclick = () => { /* Futuramente: Marcar como lida e navegar */ };
                painel.appendChild(div);
            });
        }
    });
}

