/**
 * Calcula a soma de todos os campos de peso e atualiza o ecrã.
 */
function calcularSomaPesos() {
    const displaySoma = document.getElementById('soma-pesos-valor');
    const containerDisplay = document.getElementById('total-pesos-display');
    const inputsPeso = document.querySelectorAll('#tabela-edicao-criterios .peso-input');
    
    let soma = 0;
    inputsPeso.forEach(input => {
        soma += parseFloat(input.value) || 0;
    });
    
    // CORREÇÃO: Arredonda a soma para 2 casas decimais para evitar erros de precisão
    const somaArredondada = parseFloat(soma.toFixed(2));
    
    displaySoma.textContent = somaArredondada;
    
    if (somaArredondada === 100) {
        containerDisplay.style.color = 'green';
    } else {
        containerDisplay.style.color = 'red';
    }
}
