/**
 * Grava as novas regras de critérios da avaliação na base de dados.
 */
window.gravarCriteriosAvaliacao = async function() {
    const somaPesos = parseFloat(document.getElementById('soma-pesos-valor').textContent);
    if (somaPesos !== 100) {
        alert(`A soma dos pesos é ${somaPesos}, mas deve ser exatamente 100. Por favor, ajuste os valores.`);
        return;
    }

    if (!confirm("Tem a certeza que deseja guardar estas novas regras? Elas serão aplicadas a todas as futuras auditorias.")) {
        return;
    }

    const novosCriterios = [];
    const linhas = document.querySelectorAll("#tabela-edicao-criterios tbody tr");

    linhas.forEach(linha => {
        if (linha.classList.contains('category-row') || linha.classList.contains('sub-category-row')) {
            novosCriterios.push({
                criterio: linha.cells[0].textContent,
                isCategory: linha.classList.contains('category-row'),
                isSubCategory: linha.classList.contains('sub-category-row')
            });
        } else {
            const criterioInput = linha.cells[0].querySelector('input');
            const pesoInput = linha.cells[1].querySelector('input');
            novosCriterios.push({
                criterio: criterioInput.value,
                peso: parseFloat(pesoInput.value) || 0,
                tipo: 'select' // Podemos definir um tipo padrão
            });
        }
    });

    try {
        // Guarda os novos critérios num documento específico na Firestore
        const configRef = doc(db, "configuracao", "avaliacaoConcessao");
        await updateDoc(configRef, { items: novosCriterios });

        // Atualiza a variável local para que a nova auditoria já use as novas regras
        avaliacaoConcessaoItems = novosCriterios;

        alert("Novos critérios de avaliação guardados com sucesso!");
        window.mostrarConsolaDeGestao(); // Volta para a consola
    } catch (error) {
        console.error("Erro ao guardar os critérios:", error);
        alert("Ocorreu um erro ao guardar os novos critérios. Verifique a consola.");
    }
}
/**
 * Carrega os critérios da "Avaliação da Concessão" a partir da Firestore.
 */
async function carregarCriteriosDeAvaliacao() {
    try {
        const configRef = doc(db, "configuracao", "avaliacaoConcessao");
        const docSnap = await getDoc(configRef);
        if (docSnap.exists() && docSnap.data().items) {
            console.log("Critérios de avaliação carregados da base de dados.");
            avaliacaoConcessaoItems = docSnap.data().items;
        } else {
            console.warn("Nenhuma configuração de critérios encontrada na base de dados. A usar a versão do código.");
            // Se não encontrar na BD, continua a usar a que já está no script.js como fallback.
        }
    } catch (error) {
        console.error("Erro ao carregar critérios da BD. A usar a versão do código.", error);
    }
}
