async function processarFicheiroKPIs(event) {
    const file = event.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = async function(e) {
        try {
            const data = new Uint8Array(e.target.result);
            const workbook = XLSX.read(data, { type: 'array' });
            
            const nomesFolhas = {
                margemPecas: "Margem peças",
                orsEmCurso: "ORs em curso",
                eficienciaMO: "Eficiência de MO",
                clientesInternos: "Clientes internos",
                garantias: "Garantias"
            };

            const kpiData = { margemPecas: {}, orsEmCurso: {}, eficienciaMO: {}, clientesInternos: {}, garantias: {} };

            const processarFolha = (nomeFolha, kpiKey) => {
                const worksheet = workbook.Sheets[nomeFolha];
                if (!worksheet) { console.warn(`A folha "${nomeFolha}" não foi encontrada.`); return; }
                
                const json = XLSX.utils.sheet_to_json(worksheet, { header: ['oficina', 'valor'] });

                // CORREÇÃO: Usa .slice(1) para ignorar a primeira linha (cabeçalho)
                json.slice(1).forEach(row => {
                    if (row.oficina && row.valor !== undefined && row.valor !== 'N/D' && row.valor !== 0) {
                        const valorNumerico = parseFloat(row.valor);
                        if (!isNaN(valorNumerico)) {
                             kpiData[kpiKey][row.oficina] = valorNumerico;
                        }
                    }
                });
            };

            Object.keys(nomesFolhas).forEach(key => processarFolha(nomesFolhas[key], key));
            
            dadosRankingKPIs = kpiData;
            
            const docRef = doc(db, "planeamento_kpis", "dados_atuais");
            await setDoc(docRef, { ...kpiData, timestamp_importacao: new Date() });
            
            await calcularERenderizarRanking();
            alert("Ficheiro de KPIs importado e ranking calculado com sucesso!");

        } catch (error) {
            console.error("Erro ao processar o ficheiro de KPIs:", error);
            alert("Ocorreu um erro ao ler o ficheiro Excel.");
        }
    };
    reader.readAsArrayBuffer(file);
}

// 3. FUNÇÃO PRINCIPAL QUE CALCULA E RENDERIZA O RANKING
async function calcularERenderizarRanking() {
    if (!dadosRankingKPIs) {
        alert("Por favor, carregue primeiro o ficheiro de KPIs.");
        return;
    }

    const q = query(collection(db, "auditorias"), orderBy("timestamp", "desc"));
    const querySnapshot = await getDocs(q);
    const auditoriasHistoricas = querySnapshot.docs.map(doc => doc.data());
    auditoriasHistoricasGlobal = auditoriasHistoricas;
    const todasAsOficinas = new Set();
    Object.values(dadosRankingKPIs).forEach(kpi => {
        Object.keys(kpi).forEach(oficina => todasAsOficinas.add(oficina));
    });

    const dadosFinais = [];

    for (const oficina of todasAsOficinas) {
        let kpisAplicaveis = 0;
        let somaDesviosRank = 0;

        const kpiSpecs = [
            { key: 'margemPecas', higherIsBetter: true },
            { key: 'orsEmCurso', higherIsBetter: false },
            { key: 'eficienciaMO', higherIsBetter: true },
            { key: 'clientesInternos', higherIsBetter: true },
            { key: 'garantias', higherIsBetter: false }
        ];

        kpiSpecs.forEach(spec => {
            const kpiValores = Object.values(dadosRankingKPIs[spec.key]).filter(v => !isNaN(v));
            if (kpiValores.length === 0) return;
            const mediaKPI = kpiValores.reduce((a, b) => a + b, 0) / kpiValores.length;
            const valorOficina = dadosRankingKPIs[spec.key][oficina];
            if (valorOficina !== undefined && !isNaN(valorOficina)) {
                kpisAplicaveis++;
                const desvio = valorOficina - mediaKPI;
                const desvioPonderado = spec.higherIsBetter ? -desvio : desvio;
                somaDesviosRank += desvioPonderado;
            }
        });
        
        const scoreKPI = kpisAplicaveis > 0 ? somaDesviosRank / kpisAplicaveis : 0;

        // --- CORREÇÃO AQUI: Usa a nova função parseDateString ---
        const auditoriasDaOficina = auditoriasHistoricas.filter(a => a.oficina_auditada === oficina && parseDateString(a.data_auditoria));
        
        let diasDesdeUltimaAuditoria = Infinity;
        if (auditoriasDaOficina.length > 0) {
            const ultimaAuditoria = auditoriasDaOficina.sort((a, b) => parseDateString(b.data_auditoria) - parseDateString(a.data_auditoria))[0];
            const dataUltima = parseDateString(ultimaAuditoria.data_auditoria);
            if (dataUltima) {
                 diasDesdeUltimaAuditoria = Math.floor((new Date() - dataUltima) / (1000 * 60 * 60 * 24));
            }
        }

        let classificacaoMedia = -1;
        if (auditoriasDaOficina.length > 0) {
            const auditoriasComPontuacao = auditoriasDaOficina.filter(a => a.pontuacao_final && !isNaN(parseFloat(String(a.pontuacao_final).replace('%',''))));
            if (auditoriasComPontuacao.length > 0) {
                const somaPontuacoes = auditoriasComPontuacao.reduce((acc, a) => acc + parseFloat(String(a.pontuacao_final).replace('%','')), 0);
                classificacaoMedia = somaPontuacoes / auditoriasComPontuacao.length;
            }
        }
        
        dadosFinais.push({
            oficina: oficina,
            scoreKPI: scoreKPI,
            diasDesdeUltimaAuditoria: diasDesdeUltimaAuditoria,
            classificacaoMedia: classificacaoMedia
        });
    }

    // --- Normalizar e Calcular Score Final Ponderado ---
    // Funções para normalizar os scores entre 0 e 1 (pior = 1, melhor = 0)
    const maxKPI = Math.max(...dadosFinais.map(d => d.scoreKPI));
    const minKPI = Math.min(...dadosFinais.map(d => d.scoreKPI));
    const maxDias = Math.max(...dadosFinais.filter(d => d.diasDesdeUltimaAuditoria !== Infinity).map(d => d.diasDesdeUltimaAuditoria));
    const minClassificacao = Math.min(...dadosFinais.filter(d => d.classificacaoMedia !== -1).map(d => d.classificacaoMedia));
    const maxClassificacao = Math.max(...dadosFinais.filter(d => d.classificacaoMedia !== -1).map(d => d.classificacaoMedia));

    dadosFinais.forEach(d => {
        const rankKPI = (maxKPI - minKPI) > 0 ? (d.scoreKPI - minKPI) / (maxKPI - minKPI) : 0;
        
        let rankData = 0;
        if (d.diasDesdeUltimaAuditoria !== Infinity) {
             rankData = maxDias > 0 ? d.diasDesdeUltimaAuditoria / maxDias : 0;
        }

        let rankClassificacao = 0;
        if (d.classificacaoMedia !== -1) {
            rankClassificacao = (maxClassificacao - minClassificacao) > 0 ? (maxClassificacao - d.classificacaoMedia) / (maxClassificacao - minClassificacao) : 0;
        }

        // Aplicar pesos
        if (d.diasDesdeUltimaAuditoria === Infinity) {
            // Se nunca foi auditada, o score é 100% dos KPIs
            d.scoreFinal = rankKPI * 100;
        } else {
            d.scoreFinal = (rankKPI * 30) + (rankData * 40) + (rankClassificacao * 30);
        }
    });

    // Ordenar pelo score final (maior score = maior prioridade)
    const rankingFinal = dadosFinais.sort((a, b) => b.scoreFinal - a.scoreFinal);
    rankingCalculadoGlobalmente = rankingFinal; 
    renderRankingTable(rankingFinal.slice(0, 10)); // Mostrar o Top 10
}

