window.mostrarRankingPorKPI = function(kpiKey) {
    if (!dadosRankingKPIs || !dadosRankingKPIs[kpiKey]) {
        alert("Dados para este KPI não encontrados.");
        return;
    }
    const kpiSpecs = {
        margemPecas: { name: "Margem de Peças", higherIsBetter: true, unit: '%' },
        orsEmCurso: { name: "ORs em Curso", higherIsBetter: false, unit: '€' },
        eficienciaMO: { name: "Eficiência de MO", higherIsBetter: true, unit: '%' },
        clientesInternos: { name: "Clientes Internos", higherIsBetter: true, unit: '%' },
        garantias: { name: "Nível de Garantias", higherIsBetter: false, unit: '%' }
    };
    const spec = kpiSpecs[kpiKey];
    const dados = Object.entries(dadosRankingKPIs[kpiKey])
        .map(([oficina, valor]) => ({ oficina, valor }))
        .filter(item => typeof item.valor === 'number' && !isNaN(item.valor));
    dados.sort((a, b) => { return spec.higherIsBetter ? b.valor - a.valor : a.valor - b.valor; });
    
    const modal = document.getElementById('modal-detalhes-ranking');
    const tituloModal = document.getElementById('modal-ranking-titulo');
    const corpoModal = document.getElementById('modal-ranking-corpo');
    tituloModal.textContent = `Ranking Completo - ${spec.name}`;
    
    let tabelaHTML = `<table class="audit-table"><thead><tr><th>Pos.</th><th>Oficina</th><th>Valor</th></tr></thead><tbody>`;
    dados.forEach((item, index) => {
        const valorFormatado = item.valor.toFixed(2);
        tabelaHTML += `
            <tr>
                <td><strong>${index + 1}º</strong></td>
                <td>
                    <div class="tooltip-container">
                        ${item.oficina}
                        <span class="tooltip-text">${spec.name}: ${valorFormatado}${spec.unit === '%' ? '%' : ''}</span>
                    </div>
                </td>
                <td>${valorFormatado}${spec.unit === '%' ? '%' : ''}</td>
            </tr>
        `;
    });
    tabelaHTML += `</tbody></table>`;
    corpoModal.innerHTML = tabelaHTML;
    modal.style.display = 'block';
}
// NOVO: Função para exportar os cálculos do ranking para um ficheiro Excel
async function exportarRankingParaExcel() {
    if (!rankingCalculadoGlobalmente || rankingCalculadoGlobalmente.length === 0) {
        alert("Não há dados de ranking para exportar. Por favor, importe primeiro o ficheiro de KPIs.");
        return;
    }

    alert("A preparar o ficheiro Excel detalhado... Por favor, aguarde.");

    // 1. Folha de Cálculo: Ranking Final (Resumo)
    const resumoData = rankingCalculadoGlobalmente.map((item, index) => ({
        'Posição': index + 1,
        'Oficina': item.oficina,
        'Score Final (Prioridade)': item.scoreFinal.toFixed(2),
        'Score Detalhe KPI': item.scoreKPI.toFixed(2),
        'Dias Desde Última Auditoria': item.diasDesdeUltimaAuditoria === Infinity ? 'Nunca Auditada' : item.diasDesdeUltimaAuditoria,
        'Classificação Média (%)': item.classificacaoMedia === -1 ? 'N/A' : item.classificacaoMedia.toFixed(2)
    }));
    const wsResumo = XLSX.utils.json_to_sheet(resumoData);

    // 2. Folha de Cálculo: Cálculo Detalhado do Score
    const maxKPI = Math.max(...rankingCalculadoGlobalmente.map(d => d.scoreKPI));
    const minKPI = Math.min(...rankingCalculadoGlobalmente.map(d => d.scoreKPI));
    const maxDias = Math.max(...rankingCalculadoGlobalmente.filter(d => d.diasDesdeUltimaAuditoria !== Infinity).map(d => d.diasDesdeUltimaAuditoria));
    const minClassificacao = Math.min(...rankingCalculadoGlobalmente.filter(d => d.classificacaoMedia !== -1).map(d => d.classificacaoMedia));
    const maxClassificacao = Math.max(...rankingCalculadoGlobalmente.filter(d => d.classificacaoMedia !== -1).map(d => d.classificacaoMedia));

    const detalheData = rankingCalculadoGlobalmente.map(d => {
        const rankKPI = (maxKPI - minKPI) > 0 ? (d.scoreKPI - minKPI) / (maxKPI - minKPI) : 0;
        let rankData = 1;
        if (d.diasDesdeUltimaAuditoria !== Infinity) { rankData = maxDias > 0 ? d.diasDesdeUltimaAuditoria / maxDias : 0; }
        let rankClassificacao = 1;
        if (d.classificacaoMedia !== -1) { rankClassificacao = (maxClassificacao - minClassificacao) > 0 ? (maxClassificacao - d.classificacaoMedia) / (maxClassificacao - minClassificacao) : 0; }
        
        const pesoKPI = rankKPI * 30;
        const pesoData = rankData * 40;
        const pesoClassificacao = rankClassificacao * 30;

        return {
            'Oficina': d.oficina,
            'Componente KPI (Peso 30%)': pesoKPI.toFixed(2),
            'Componente Data (Peso 40%)': pesoData.toFixed(2),
            'Componente Classificação (Peso 30%)': pesoClassificacao.toFixed(2),
            'Cálculo': `(${pesoKPI.toFixed(2)} + ${pesoData.toFixed(2)} + ${pesoClassificacao.toFixed(2)})`,
            'Score Final': d.scoreFinal.toFixed(2)
        };
    });
    const wsDetalhe = XLSX.utils.json_to_sheet(detalheData);

    // 3. Folha de Cálculo: Dados Brutos dos KPIs
    const kpiKeys = ['margemPecas', 'orsEmCurso', 'eficienciaMO', 'clientesInternos', 'garantias'];
    const oficinas = [...new Set(rankingCalculadoGlobalmente.map(item => item.oficina))];
    const kpiDataExport = oficinas.map(oficina => {
        const row = { 'Oficina': oficina };
        kpiKeys.forEach(key => {
            row[key] = dadosRankingKPIs[key]?.[oficina] ?? 'N/A';
        });
        return row;
    });
    const wsKPIs = XLSX.utils.json_to_sheet(kpiDataExport);

    // 4. Folha de Cálculo: Histórico de Auditorias Utilizado
    const historicoData = auditoriasHistoricasGlobal.map(a => ({
        'Oficina': a.oficina_auditada,
        'Data Auditoria': a.data_auditoria,
        'Pontuação Final': a.pontuacao_final,
        'Estado': a.estado,
        'ID Visita': a.id_visita
    }));
    const wsHistorico = XLSX.utils.json_to_sheet(historicoData);

    // Criar o Livro de Excel e Adicionar as Folhas
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, wsResumo, "Ranking Final");
    XLSX.utils.book_append_sheet(wb, wsDetalhe, "Cálculo Detalhado");
    XLSX.utils.book_append_sheet(wb, wsKPIs, "Dados Brutos KPIs");
    XLSX.utils.book_append_sheet(wb, wsHistorico, "Histórico de Auditorias");

    // Fazer o Download do Ficheiro
    const dataAtual = new Date().toISOString().slice(0, 10);
    XLSX.writeFile(wb, `Export_Ranking_Auditorias_${dataAtual}.xlsx`);
}
